@custom-variant dark (&:where(.dark, .dark *));

@theme {
  --font-sans: "Inter", ui-sans-serif, system-ui, sans-serif;

  /* Wordle greens */
  --color-wl-correct: #6aaa64;
  --color-wl-present: #c9b458;
  --color-wl-absent: #787c7e;
  --color-wl-tile-border: #d3d6da;
  --color-wl-tile-active: #878a8c;
  --color-wl-key-bg: #d3d6da;
}

* {
  box-sizing: border-box;
}

html {
  font-family: var(--font-sans);
}

body {
  margin: 0;
  min-height: 100dvh;
  @apply bg-white text-gray-900 dark:bg-gray-900 dark:text-gray-100;
}

#root {
  min-height: 100dvh;
  display: flex;
  flex-direction: column;
}

/* Dark mode color overrides */
:root {
  --color-correct: #6aaa64;
  --color-present: #c9b458;
  --color-absent: #787c7e;
}

.dark {
  --color-correct: #538d4e;
  --color-present: #b59f3b;
  --color-absent: #3a3a3c;
}

/* High contrast (colorblind) mode */
.high-contrast {
  --color-correct: #f5793a;
  --color-present: #85c0f9;
}

/* Tile flip animation */
@keyframes flip-in {
  0% { transform: rotateX(0deg); }
  100% { transform: rotateX(-90deg); }
}

@keyframes flip-out {
  0% { transform: rotateX(-90deg); }
  100% { transform: rotateX(0deg); }
}

.tile-flip-in {
  animation: flip-in 0.25s ease-in forwards;
}

.tile-flip-out {
  animation: flip-out 0.25s ease-out forwards;
}

/* Row shake animation for invalid words */
@keyframes shake {
  0%, 100% { transform: translateX(0); }
  10%, 30%, 50%, 70%, 90% { transform: translateX(-4px); }
  20%, 40%, 60%, 80% { transform: translateX(4px); }
}

.row-shake {
  animation: shake 0.5s ease-in-out;
}

/* Bounce for winning row */
@keyframes bounce-tile {
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-20px); }
}

.tile-bounce {
  animation: bounce-tile 0.4s ease-in-out;
}

/* Pop animation when typing */
@keyframes pop {
  0% { transform: scale(1); }
  50% { transform: scale(1.1); }
  100% { transform: scale(1); }
}

.tile-pop {
  animation: pop 0.1s ease-in-out;
}

/* Toast animation */
@keyframes toast-in {
  0% { opacity: 0; transform: translateY(-10px); }
  100% { opacity: 1; transform: translateY(0); }
}

@keyframes toast-out {
  0% { opacity: 1; transform: translateY(0); }
  100% { opacity: 0; transform: translateY(-10px); }
}

.toast-enter {
  animation: toast-in 0.2s ease-out;
}

.toast-exit {
  animation: toast-out 0.2s ease-in forwards;
}
