{"filePath": "/Users/al/Projects/my_wordle/samples/app-wordle/check-words.mjs", "content": "import { readFileSync } from 'fs';

function checkFile(filename, label) {
  const content = readFileSync(filename, 'utf8');
  // Extract the array content between [ and ]
  const match = content.match(/\[[\s\S]+?\]/);
  if (!match) { console.log('No array found in', filename); return; }
  const words = match[0]
    .slice(1, -1)
    .split(',')
    .map(w => w.trim().replace(/["']/g, ''))
    .filter(Boolean);

  const bad = words.filter(w => !/^[a-z]{5}$/.test(w));
  console.log(`\n=== ${label} ===`);
  console.log(`Total: ${words.length}, Bad: ${bad.length}`);
  bad.forEach(w => console.log(`  BAD: "${w}"`));

  const s = new Set(words);
  console.log(`Unique: ${s.size}, Dupes: ${words.length - s.size}`);
  const seen = {};
  words.forEach(w => { seen[w] = (seen[w]||0)+1; });
  Object.entries(seen).filter(([,c]) => c > 1).forEach(([w,c]) => console.log(`  DUP: "${w}" x${c}`));
}

checkFile('./src/data/words.ts', 'ANSWER_WORDS');
checkFile('./src/data/validGuesses.ts', 'EXTRA_GUESSES');
"}

